  constructor(props) {
    super(props);
    this.state = { isEmptyState: true };
  }

  triggerCheckSurvey = () => {
    this.setState({
      ...this.state,
      isEmptyState: false,
      isCheckSurveyState: true
    });
  };

  render() {
    return (
      <div>
        {this.state.isEmptyState && (
          <Home checkSurvey={this.triggerCheckSurvey} />
        )}

        {this.state.isCheckSurveyState && <Results />}
        <br></br>
        {this.state.isEmptyState && (
          <Home checkSurvey={this.triggerCheckSurvey} />
        )}

        {this.state.isCheckSurveyState && <Survey />}
      </div>
    );
  }
}

export default App;
