import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "../layouts/Header";
import Footer from "../layouts/Footer";
import { render } from "react-dom";

import Survey from "../Survey";
import Results from "../Results";

//Main component that contains Survey and Survey results button

class Home extends Component {
  constructor() {
    super();
    this.state = {
      name: "Survey",
      showHideSurvey: false,
      showHideResults: false
    };
    this.hideComponent = this.hideComponent.bind(this);
  }

  hideComponent(name) {
    console.log(name);
    switch (name) {
      case "showHideSurvey":
        this.setState({ showHideSurvey: !this.state.showHideSurvey });
        break;
      case "showHideResults":
        this.setState({ showHideResults: !this.state.showHideResults });
        break;
      default:
        null;
    }
  }

  render() {
    const { showHideSurvey, showHideResults } = this.state;
    return (
      <div className="App">
        <Header />
        {showHideSurvey && <Survey />}
        {showHideResults && <Results />}
        <button
          className="button-one"
          onClick={() => this.hideComponent("showHideSurvey")}
        ></button>
        <button
          className="button-one"
          onClick={() => this.hideComponent("showHideResults")}
        ></button>
        <Footer />
      </div>
    );
  }
}

export default Home;
