import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import Survey from "../Survey";

//Main component that contains Survey and Survey results button

class Home extends Component {
  constructor() {
    super();
    this.state = {
      name: "Survey",
      showHideSurvey: false,
      showHideResults: false
    };
    this.hideComponent = this.hideComponent.bind(this);
  }

  hideComponent(name) {
    console.log(name);
    switch (name) {
      case "showHideSurvey":
        this.setState({ showHideSurvey: !this.state.showHideSurvey });
        break;
      // case "showHideResults":
      //   this.setState({ showHideResults: !this.state.showHideResults });
      //   break;
      default:
        null;
    }
  }

  render() {
    const { showHideSurvey, showHideResults } = this.state;
    return (
      <div>
        {showHideSurvey && <Survey />}
        <hr />
        <div>
          <button onClick={() => this.hideComponent("showHideSurvey")}></button>
        </div>
      </div>
    );
  }
}

export default Home;
