import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { createPost } from "../../../actions/posts";

const Survey = () => {
  const [postData, setPostData] = useState({
    surname: "",
    firstNames: "",
    contactNumber: null,
    date: "",
    age: null,

    food: [
      "pizza",
      "pasta",
      "Pap and Wors",
      "Chicken stir fry",
      "Beef stir fry",
      "Other"
    ]
  });

  //allows us to dispatch actions
  const dispatch = useDispatch();

  const handleSubmit = () => {
    dispatch(createPost(postData));
  };

  // const handleInputChange = event => {
  //   dispatch(createPost(postData));

  //   const target = event.target;
  //   const value = target.type === "checkbox" ? target.checked : target.value;
  //   const name = target.name;

  //   setPostData({
  //     [name]: value
  //   });
  // };

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <p>Take our Survey</p>
        <p>Personal Details:</p>
        <p>
          <input
            type="text"
            placeholder="Surname"
            name="surname"
            value={postData.surname}
            onChange={e =>
              setPostData({ ...postData, surname: e.target.value })
            }
          />
        </p>
        <p>
          <input
            type="text"
            placeholder="First Names"
            name="firstNames"
            value={postData.firstNames}
            onChange={e =>
              setPostData({ ...postData, firstNames: e.target.value })
            }
          />
        </p>
        <p>
          <input
            type="text"
            placeholder="Contact number"
            name="contactNumber"
            value={postData.contactNumber}
            onChange={e =>
              setPostData({ ...postData, contactNumber: e.target.value })
            }
          />
        </p>
        <p>
          <input
            type="text"
            placeholder="Date"
            name="date"
            value={postData.date}
            onChange={e => setPostData({ ...postData, date: e.target.value })}
          />
        </p>
        <p>
          <input
            type="text"
            placeholder="Age"
            name="age"
            value={postData.age}
            onChange={e => setPostData({ ...postData, age: e.target.value })}
          />
        </p>
        <p>What is your favourite food? (You can choose more than 1 answer)</p>
        <div>
          <p>
            <input
              label="Pizza"
              type="checkbox"
              value={postData.food[0]}
              name="pizza"
              // checked={postData.food}
              onChange={e => setPostData({ ...postData, food: e.target.value })}
            />
            Pizza
          </p>
          <p>
            <input
              type="checkbox"
              value={postData.food}
              // checked={postData.food}
              name="pasta"
              onChange={e => setPostData({ ...postData, food: e.target.value })}
            />{" "}
            Pasta
          </p>
          <p>
            <input
              type="checkbox"
              value={postData.food}
              name="Pap and Wors"
              // checked={postData.food}
              onChange={e => setPostData({ ...postData, food: e.target.value })}
            />{" "}
            Pap and Wors
          </p>
          <p>
            <input
              type="checkbox"
              value={postData.food}
              name="Chicken stir fry"
              onChange={e =>
                setPostData({ ...postData, firstNames: e.target.value })
              }
            />{" "}
            Chicken stir fry
          </p>
          <p>
            <input
              type="checkbox"
              value={postData.food}
              name="Beef stir fry"
              onChange={e => setPostData({ ...postData, food: e.target.value })}
            />{" "}
            Beef stir fry
          </p>
          <p>
            <input
              type="checkbox"
              value={postData.food}
              name="other"
              onChange={e => setPostData({ ...postData, food: e.target.value })}
            />{" "}
            Other
          </p>
        </div>
        <br></br>
        On a scale of 1 to 5 indicate whether you strongly agree to strongly
        disagree
        <br></br>
        <br></br>
        <table>
          <thead>
            <tr>
              <th></th>
              <th>
                Strongly Agree<br></br> (1)
              </th>{" "}
              <th>
                Agree<br></br>(2)
              </th>
              <th>
                Neutral<br></br>(3)
              </th>
              <th>
                Disagree<br></br>(4)
              </th>
              <th>
                Strongly disagree<br></br>(5)
              </th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>I like to eat out</td>
              <td>
                <input type="radiobutton" value={postData.food} name="other" />
              </td>
              <td></td>
              <td></td>
            </tr>
            <tr>
              <td>I like to watch movies </td>
              <td></td>
              <td></td>
              <td></td>
            </tr>
            <tr>
              <td>I like to watch TV</td>
              <td colSpan="2"></td>
              <td></td>
            </tr>
            <tr>
              <td>I like to listen to the radio </td>
              <td colSpan="2"></td>
              <td></td>
            </tr>
          </tbody>
        </table>
        <br></br>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Survey;
