// export default Survey;

class Survey extends Component {
  constructor(props) {
    super(props);

    // Set initial state
    this.state = { msg: "Hi, There!" };

    // Binding this keyword
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    // Changing state
    this.setState({ msg: "Welcome to the React world!" });
  }

  render() {
    return (
      <div className="App">
        <Header />
        {/* <Survey /> */}
        <button onClick={this.handleClick} className="button-one">
          <strong>Fill out Survey</strong>
        </button>
        <br />
        <p>{this.state.msg}</p>
        <button className="button-one">
          <strong>View Survey Results</strong>
        </button>
        <Footer />
      </div>
    );
  }
}

export default Survey;
