import React, { Component } from "react";

class Survey extends Component {
  constructor(props) {
    super(props);

    this.state = {
      surname: null
    };
  }

  handleSubmit = event => {
    event.preventDefault();
    const data = this.state;
  };

  handleInput = event => {
    // Changing state
    event.preventDefault();

    this.setState({
      [event.target.name]: event.target.value
    });
  };

  render() {
    const { surname } = this.state;
    return (
      <div className="App">
        <p>{surname}</p>
        <form onSubmit={this.handleSubmit}>
          <p>Take our Survey</p>
          <br></br>
          <p>Personal Details:</p>
          <p>
            <input
              type="text"
              placeholder="Surname"
              name="surname"
              onChange={this.handleInput}
            />
          </p>
          <p>
            <input
              type="text"
              placeholder="First Names"
              name="firstNames"
              onChange={this.handleInput}
            />
          </p>
          <p>
            <input
              type="text"
              placeholder="Contact number"
              name="contactNumber"
              onChange={this.handleInput}
            />
          </p>
          <p>
            <input
              type="text"
              placeholder="Date"
              name="date"
              onChange={this.handleInput}
            />
          </p>
          <p>
            <input
              type="text"
              placeholder="Age"
              name="age"
              onChange={this.handleInput}
            />
          </p>
          <p>
            What is your favourite food? (You can choose more than 1 answer){" "}
          </p>
          <p>
            <input label="Pizza" type="radio" value="Pizza" name="food" />
            Pizza
          </p>
          <p>
            <input type="radio" value="Pasta" name="food" />
          </p>
          <p>
            <input type="radio" value="Pap and Wors" name="food" />
          </p>
          <p>
            <input type="radio" value="Chicken stir fry" name="food" />
          </p>
          <p>
            <input type="radio" value="Beef stir fry" name="food" />
          </p>
          <p>
            <input type="radio" value="Other" name="food" />
          </p>
          <button onClick={this.handleSubmit}></button>
        </form>
      </div>
    );
  }
}

export default Survey;
