import "./App.css";
import React, { Component, useEffect, useState } from "react";
import Home from "./components/regions/Home";
import Survey from "./components/regions/Survey";
import Results from "./components/regions/Results";
import { useDispatch } from "react-redux";

const App = props => {
  const dispatch = useDispatch();

  const [isEmpty, setEmpty] = useState(true);
  const [checkSurvey, setSurveyState] = useState(false);
  const [checkResults, setResultsState] = useState(false);

  triggerCheckSurvey = () => {
    setEmpty({
      isEmpty: false
    });
    setSurveyState({
      checkSurvey: true
    });
  };

  triggerCheckResults = () => {
    setEmpty({
      isEmpty: false
    });
    setResultsState({
      checkResults: true
    });
  };

  // ueseEffect(() => {});

  return (
    <div>
      {props.state.isEmpty && (
        <Home
          checkSurvey={triggerCheckSurvey}
          checkResults={triggerCheckResults}
        />
      )}

      {props.state.CheckResults && <Results />}
      {props.state.CheckSurvey && <Survey />}

      <br></br>
    </div>
  );
};

